// src/store/index.js
import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    accessToken: null,
    userId: null,
  },
  mutations: {
    setAccessToken(state, token) {
      state.accessToken = token;
    },
    setUserId(state, userId) {
      state.userId = userId;
    },
  },
  actions: {
    login({ commit }, { token, userId }) {
      commit('setAccessToken', token);
      commit('setUserId', userId);
    },
  },
});
